    .enum(["summary-card", "bar-chart", "line-chart", "data-table", "result-grid"])
    .default("result-grid")
    .describe(
      "UI format for rendering results. summary-card: KPIs/aggregates; bar-chart: categorical comparisons; line-chart: time series trends; data-table: detailed rows; result-grid: combined summary + chart + table."
    ),
});

type MySqlQueryInput = z.infer<typeof inputSchema>;

type MySqlQueryResult = {
  success: boolean;
  message: string;
  rows?: unknown[] | null;
  error?: string;
  meta?: {
    query: string;
    elapsedMs: number;
    database?: string;
  };
};

type RowRecord = Record<string, unknown>;

type ChartMeta<T> = {
  data: T[];
  labelKey?: string;
  valueKey?: string;
};

const timeKeyRegex = /(time|date|timestamp)/i;

function isRecord(value: unknown): value is RowRecord {
  return Boolean(value) && typeof value === "object" && !Array.isArray(value);
}

function getRowRecords(rows: unknown[] | null): RowRecord[] {
  if (!rows) return [];
  return rows.filter(isRecord);
}

function toNumber(value: unknown): number | null {
  if (typeof value === "number" && Number.isFinite(value)) {
    return value;
  }
  if (typeof value === "string") {
    const trimmed = value.trim();
    if (!trimmed) return null;
    const num = Number(trimmed);
    return Number.isFinite(num) ? num : null;
  }
  return null;
}

function formatCellValue(value: unknown): React.ReactNode {
  if (value === null || value === undefined) return "—";
  if (value instanceof Date) return value.toISOString();
  if (typeof value === "string" || typeof value === "number" || typeof value === "boolean") {
    return String(value);
  }
  try {
    return JSON.stringify(value);
  } catch {
    return String(value);
  }
}

function buildTableColumns(sampleRow: RowRecord): DbQueryTableColumn<RowRecord>[] {
  return Object.keys(sampleRow).map((key) => ({
    key,
    header: key,
    accessor: (row) => formatCellValue(row[key]),
  }));
}

function inferSummaryType(key: string): DbQuerySummaryMetric["type"] {
  const lower = key.toLowerCase();
  if (lower.includes("total")) return "totalRecords";
  if (lower.includes("count")) return "count";
  if (lower.includes("sum")) return "sum";
  if (lower.includes("avg") || lower.includes("average")) return "average";
  if (lower.includes("min")) return "min";
  if (lower.includes("max")) return "max";
  return "count";
}

function buildSummaryMetrics(rows: RowRecord[]): DbQuerySummaryMetric[] {
  const firstRow = rows[0];
  if (!firstRow) return [];

  return Object.keys(firstRow)
    .map((key) => {
      const value = toNumber(firstRow[key]);
      if (value === null) return null;
      return {
        label: key,
        value,
        type: inferSummaryType(key),
      } satisfies DbQuerySummaryMetric;
    })
    .filter((entry): entry is DbQuerySummaryMetric => Boolean(entry));
}

function selectValueKey(sampleRow: RowRecord): string | undefined {
  return Object.keys(sampleRow).find((key) => toNumber(sampleRow[key]) !== null);
}

function selectLabelKey(sampleRow: RowRecord, valueKey?: string, preferTime = false) {
  const keys = Object.keys(sampleRow).filter((key) => key !== valueKey);
  if (preferTime) {
    const timeKey = keys.find((key) => timeKeyRegex.test(key));
    if (timeKey) return timeKey;
  }
  return keys[0];
}

function buildBarChartData(rows: RowRecord[]): ChartMeta<DbQueryBarDatum> {
  const sampleRow = rows[0];
  if (!sampleRow) return { data: [] };

  const valueKey = selectValueKey(sampleRow);
  const labelKey = selectLabelKey(sampleRow, valueKey, false);

  if (!valueKey) return { data: [] };

  const data = rows
    .map((row, index) => {
      const value = toNumber(row[valueKey]);
      if (value === null) return null;
      const label = labelKey ? String(row[labelKey] ?? `Row ${index + 1}`) : `Row ${index + 1}`;
      return { label, value } satisfies DbQueryBarDatum;
    })
    .filter((entry): entry is DbQueryBarDatum => Boolean(entry));

  return { data, labelKey, valueKey };
}

function buildLineChartData(rows: RowRecord[]): ChartMeta<DbQueryLineDatum> {
  const sampleRow = rows[0];
  if (!sampleRow) return { data: [] };

  const valueKey = selectValueKey(sampleRow);
  const labelKey = selectLabelKey(sampleRow, valueKey, true);

  if (!valueKey) return { data: [] };

  const data = rows
    .map((row, index) => {
      const value = toNumber(row[valueKey]);
      if (value === null) return null;
      const timestamp = labelKey ? (row[labelKey] ?? `Row ${index + 1}`) : `Row ${index + 1}`;
      return { timestamp, value } satisfies DbQueryLineDatum;
    })
    .filter((entry): entry is DbQueryLineDatum => Boolean(entry));

  return { data, labelKey, valueKey };
}

async function createClient(params: MySqlQueryInput): Promise<Connection> {
  if (params.url) {
    return await createConnection(params.url);
  }
  return await createConnection({
    host: params.host,
    port: params.port,
    user: params.user,
    password: params.password,
    database: params.database,
  });
}

