    if (!query) {
      return {
        success: false,
        message: "SQL is empty.",
        error: "Empty SQL",
      };
    }

    const startedAt = Date.now();
    let connection: Connection | null = null;

    try {
      connection = await createClient(params);
      const result = await connection.query(query);
      const tuple = result as unknown as [QueryResult, FieldPacket[]];
      const normalized = normalizeQueryResult(tuple);

      const response: MySqlQueryResult = {
        success: true,
        message: "Query completed.",
        rows: normalized.rows,
        meta: {
          query,
          elapsedMs: Date.now() - startedAt,
          database: params.database,
        },
      };

      return response;
    } catch (error) {
      const message = error instanceof Error ? error.message : String(error);
      return {
        success: false,
        message: "Query failed. Check connection settings and SQL.",
        error: message,
      };
    } finally {
      if (connection) {
        try {
