              }
              chartType={lineMeta.data.length ? "line" : "bar"}
              emptyMessage="No rows"
            />
          );
      }
    })();

    return (
      <div className="space-y-3 rounded-md border border-muted bg-background p-4 text-sm">
        <div className="flex flex-wrap items-center gap-2 text-muted-foreground">
