   */
  private addLog(level: ExecutionLog['level'], message: string, data?: unknown): void {
    const log: ExecutionLog = {
      id: `log_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: Date.now(),
      level,
      message,
      data,
    };
    this.logs.push(log);
  }

  /**
   * Get all execution logs
   */
  getLogs(): ExecutionLog[] {
    return [...this.logs];
